import React from 'react'
import styled from 'styled-components'

const Container = styled.div`
    height: 30px;
    background-color: teal;
    color: white;
    display: flex;
    align-items: center;
    padding: 3px;

`

const Announcements = () => {
  return (
      <Container>
          Super deal! Free Shipping on all Orders Over $50
    </Container>
  )
}

export default Announcements
